<?phpnamespace ADBundle\Service;use ADBundle\Entity\User;/** * Description of AD * * @author Yann */class ActiveDirectory{    protected $host = "636";    protected $hosts = "636";    protected $ipServer;    protected $ldapUser;    protected $ldapPass;    protected $ldapConnect;    protected $ldapBind;//    protected $ldapTree = "OU=ComptesUtilisateur,DC=chez,DC=mtvc";    //protected $ldapTree = "DC=42groupe,DC=cloud";    protected $ldapTree = "DC=42group,DC=cloud";    /**     * @param $ipServer     * @param $ldapUser     * @param $ldapPass     */    public function __construct($ipServer, $ldapUser, $ldapPass)    {        $this->ipServer = $ipServer;        $this->ldapUser = $ldapUser;        $this->ldapPass = $ldapPass;        $this->ldapConnect = \ldap_connect("ldaps://" . $this->ipServer, $this->host) or die("Could not connect to LDAP server.");        ldap_set_option($this->ldapConnect, LDAP_OPT_PROTOCOL_VERSION, 3);        ldap_set_option($this->ldapConnect, LDAP_OPT_REFERRALS, 0);        if ($this->ldapConnect) {            $this->ldapBind = @ldap_bind($this->ldapConnect, $this->ldapUser, $this->ldapPass) or die ("Error trying to bind: " . ldap_error($this->ldapConnect));        }    }    /**     * @param $ldapUser     * @param $password     * @return bool     */    function checkSession($ldapUser, $password)    {        $result = false;        if ($this->ldapConnect) {            $result = @ldap_bind($this->ldapConnect, $ldapUser, $password);        }        return $result;    }    public function getLdapConnect()    {        if (!$this->ldapConnect) {            return ldap_connect("ldaps://" . $this->ipServer, $this->host) or die("Could not connect to LDAP server.");        }        return $this->ldapConnect;    }    function getAllUser($OU = null)    {        $data = null;        if ($this->ldapConnect && $this->ldapBind) {            $filter = "(&(objectClass=User))";            if ($OU == null) {                $tree = $this->ldapTree;            } else {                $tree = "OU=" . $OU . "," . $this->ldapTree;            }            $result = ldap_search($this->ldapConnect, $tree, $filter) or die ("Error in search query: " . ldap_error($this->ldapConnec));            $data = ldap_get_entries($this->ldapConnect, $result);        }        return $data;    }    function getUser($person)    {        $data = null;        if ($this->ldapConnect && $this->ldapBind) {            $filter = "(&(objectClass=User))";            $result = ldap_search($this->ldapConnect, $person, $filter) or die ("Error in search query: " . ldap_error($this->ldapConnec));            $data = ldap_get_entries($this->ldapConnect, $result);        }        return $data;    }    function getUserByLogin($login)    {        $data = null;        if ($this->ldapConnect && $this->ldapBind) {            $filter = "(&(objectCategory=person)(objectClass=User)(sAMAccountName=$login))";            $result = ldap_search($this->ldapConnect, $this->ldapTree, $filter)            or die ("Error in search query: " . ldap_error($this->ldapConnec));            $data = ldap_get_entries($this->ldapConnect, $result);        }        return $data;    }    function getUserByUserPrincipalName($username)    {        $data = null;        if ($this->ldapConnect && $this->ldapBind) {            $filter = "(&(objectCategory=person)(objectClass=User)(userPrincipalName=$username))";            $result = ldap_search($this->ldapConnect, $this->ldapTree, $filter)            or die ("Error in search query: " . ldap_error($this->ldapConnec));            $data = ldap_get_entries($this->ldapConnect, $result);        }        return $data;    }    function removeUser($ldap_tree)    {        $result = false;        if ($this->ldapConnect && $this->ldapBind) {            $result = @ldap_delete($this->ldapConnect, $ldap_tree);            ldap_close($this->ldapConnect);        }        return $result;    }    function addUser(User $user)    {        $result = null;        if ($this->ldapConnect && $this->ldapBind) {            $info["cn"] = ucname("{" . $user->getFullName() . " " . $user->getFirstName() . "}");//            $info["sn"] = $user->getLogin();//            $info["objectclass"] = "person";            $info["streetAddress"] = $user->getStreet() . " " . $user->getAddress(); //Rue            $info["l"] = $user->getCity(); //Ville            $info["st"] = $user->getState(); //Département            $info["postalCode"] = $user->getState(); //Code postal            //$info["c"] = $_POST['country']; //Pays            $info["givenName"] = ucname("{$user->getFullName()}"); //Prénom            $info["sn"] = ucname("{$user->getLogin()}"); //Nom            $info["displayName"] = ucname("{" . $user->getFullName() . " " . $user->getFirstName() . "}");            $info["description"] = "test";            $info["physicalDeliveryOfficeName"] = $user->getOffice(); //Bureau            $info["telephoneNumber"] = $user->getPhone(); //Numéro de téléphone            $info["userPrincipalName"] = $this->ldapUser; //Logon Name            $info["mail"] = $user->getEmail(); //Adresse mail	            $info["objectClass"] = "person"; //Type            $info["useraccountcontrol"][0] = 544; //Activation du compte            $info["userPassword"] = "{SHA}" . base64_encode(pack("H*", sha1($user->getPassword()))); //Mot de passe//            if ($OU == 2) {//                $info["accountExpires"] = "9223372036854775807";//                $OU = "Users";//            } else if ($OU == 5) {//                $OU = "External";//                $info["accountExpires"] = "130834584000000000";//            } else {//                $OU = "Users";//                $info["accountExpires"] = "130834584000000000";//            }            $ldap_tree = "CN=" . $info["cn"] . "," . $this->ldapTree;            if ($this->personExist($info["cn"])) {                return false;            }            $result = @ldap_add($this->ldapConnect, $ldap_tree, $info);            ldap_close($this->ldapConnect);        }        return $result;    }    function changePasswordUser(User $user)    {        if ($this->checkSession($user->getLogin(), $user->getOldPassword())) {            putenv('LDAPTLS_REQCERT=never');            $ldapConnect = ldap_connect('ldaps://' . $this->ipServer . ':636') or die("Could not connect to LDAP server.");            ldap_set_option($ldapConnect, LDAP_OPT_PROTOCOL_VERSION, 3);            ldap_set_option($ldapConnect, LDAP_OPT_REFERRALS, 0);            if ($ldapConnect) {                $ldapBind = @ldap_bind($ldapConnect, $this->ldapUser, $this->ldapPass);                if ($ldapBind) {                    $newPassword = "\"" . $user->getPassword() . "\"";                    $newPassw = "";                    $len = strlen($newPassword);                    for ($i = 0; $i < $len; $i++)                        $newPassw .= "{$newPassword{$i}}\000";                    $newPassword = $newPassw;                    $userdata["unicodepwd"] = $newPassword;                    $result = ldap_mod_replace($ldapConnect, $user->getDn(), $userdata);                    ldap_close($ldapConnect);                    if ($result)                        return "success";                    else                        return "Erreur s'est produit lors de la modification : " . ldap_error($this->ldapConnect);                } else {                    return "Erreur lors de l'exécution de la fonction ldap_bind(): " . ldap_error($this->ldapConnect);                }            } else {                return "Erreur: Pas de Connexion LDAP Server";            }        } else {            return "Ancien mot de passe incorrect";        }    }    function testLdaps()    {        if ($this->checkSession($this->ldapUser, $this->ldapPass)) {            putenv('LDAPTLS_REQCERT=never');            $ldapConnect = ldap_connect('ldaps://' . $this->ipServer . ':636') or die("Could not connect to LDAP server.");            ldap_set_option($ldapConnect, LDAP_OPT_PROTOCOL_VERSION, 3);            ldap_set_option($ldapConnect, LDAP_OPT_REFERRALS, 0);            if ($ldapConnect) {                $ldapBind = @ldap_bind($ldapConnect, $this->ldapUser, $this->ldapPass);                if ($ldapBind) {                    dump($ldapBind);                    die("OKKKKKK");                }                die("Erreur ldap_bind(): ");            }            die("Erreur ldap_connect(): " . ldap_error($this->ldapConnect));        }        die('No Connect');    }    function personExist($user)    {        if ($this->ldapConnect && $this->ldapBind) {            if (@ldap_search($this->ldapConnect, "CN=" . $user . "," . $this->ldapTree, "(cn=*)")) {                return true;            }        }        return false;    }    /* Functions */    function getData($array, $attr)    {        return isset($array[$attr][0]) ? $array[$attr][0] : " ";    }    /**     * @param $data     * @return string     */    function base64Encode($data)    {        return rtrim(strtr(base64_encode($data), '+/', '-_'), '=');    }    /**     * @param $data     * @return string     */    function base64Decode($data)    {        return base64_decode(str_pad(strtr($data, '-_', '+/'), strlen($data) % 4, '=', STR_PAD_RIGHT));    }}